mapper.delete:
DELETE FROM class_schedule
WHERE course_id = #{courseId}
  AND teacher_id = #{teacherId}
  AND class_time = #{classTime};

mapper.select:
SELECT cs.schedule_id, cs.course_id, c.course_name, c.credit, cs.teacher_id, t.name as teacher_name, t.position, cs.class_time, cs.classroom, cs.capacity, (SELECT COUNT(*) FROM enrollment e WHERE e.schedule_id = cs.schedule_id) as enrolled_count FROM class_schedule cs JOIN course c ON cs.course_id = c.course_id JOIN teacher t ON cs.teacher_id = t.teacher_id JOIN department d ON c.department_id = d.department_id
cs.status = '审核通过'
AND cs.course_id = #{courseId}
AND c.course_name LIKE CONCAT('%', #{courseName}, '%')
AND cs.teacher_id = #{teacherId}
AND t.name LIKE CONCAT('%', #{teacherName}, '%')
AND d.department_name = #{departmentName}
AND c.credit = #{credit}
AND cs.class_time LIKE CONCAT('星期', #{weekday}, '%')
AND cs.class_time LIKE CONCAT('%', #{timeSlot})
AND (cs.capacity - (SELECT COUNT(*) FROM enrollment e WHERE e.schedule_id = cs.schedule_id)) >= #{capacityMin}
AND (cs.capacity - (SELECT COUNT(*) FROM enrollment e WHERE e.schedule_id = cs.schedule_id)) <= #{capacityMax}
AND (SELECT COUNT(*) FROM enrollment e WHERE e.schedule_id = cs.schedule_id) < cs.capacity
ORDER BY cs.course_id, t.teacher_id

SELECT * FROM class_schedule
WHERE course_id = #{courseId}
AND teacher_id = #{teacherId}
AND class_time = #{classTime}
AND status = '审核通过';

SELECT cs.schedule_id, cs.semester, cs.course_id, c.course_name, c.credit, cs.teacher_id, t.name as teacher_name, cs.class_time, cs.classroom, cs.capacity, cs.status FROM class_schedule cs LEFT JOIN course c ON cs.course_id = c.course_id LEFT JOIN teacher t ON cs.teacher_id = t.teacher_id
AND cs.schedule_id = #{scheduleId}
AND cs.semester = #{semester}
AND cs.course_id = #{courseId}
AND c.course_name LIKE CONCAT('%', #{courseName}, '%')
AND cs.teacher_id = #{teacherId}
AND t.name LIKE CONCAT('%', #{teacherName}, '%')
AND cs.status = #{status}
ORDER BY cs.schedule_id ASC
