mapper.update:
UPDATE exam
SET exam_time = #{examTime}, exam_place = #{examPlace}
WHERE schedule_id = #{scheduleId};

mapper.select:
SELECT e.exam_id, e.schedule_id, cs.course_id, c.course_name, cs.teacher_id, t.name AS teacher_name, e.exam_time, e.exam_place FROM exam e LEFT JOIN class_schedule cs ON e.schedule_id = cs.schedule_id LEFT JOIN course c ON cs.course_id = c.course_id LEFT JOIN teacher t ON cs.teacher_id = t.teacher_id
AND e.exam_id = #{examId}
AND e.schedule_id = #{scheduleId}
AND cs.course_id = #{courseId}
AND c.course_name LIKE CONCAT('%', #{courseName}, '%')
AND cs.teacher_id = #{teacherId}
AND t.name LIKE CONCAT('%', #{teacherName}, '%')
AND e.exam_time = #{examTime}
AND e.exam_place LIKE CONCAT('%', #{examPlace}, '%')
ORDER BY e.exam_id
