mapper.select:
SELECT
s.student_id,
s.password,
s.name,
s.gender,
s.email,
s.grade_number,
s.native_place,
d.department_name,
m.major_name,
s.status
FROM student s
LEFT JOIN department d ON s.department_id = d.department_id
LEFT JOIN major m ON s.major_id = m.major_id


        AND s.student_id = #{studentId}


        AND s.name LIKE CONCAT('%', #{studentName}, '%')


        AND s.gender = #{gender}


        AND s.email = #{email}


        AND s.grade_number = #{gradeNumber}


        AND s.native_place = #{nativePlace}


        AND s.department_id = #{departmentId}


        AND s.major_id = #{majorId}


        AND s.status = #{status}


ORDER BY s.student_id

mapper.insert:
INSERT INTO student (
    student_id, password, name, gender, email,
    grade_number, native_place, department_id, major_id, status
) VALUES (
            #{studentId}, #{password}, #{name}, #{gender}, #{email},
             #{gradeNumber}, #{nativePlace}, #{departmentId}, #{majorId}, #{status}
         )
