mapper.insert:
INSERT INTO teacher (
    password, name, email, position, gender, department_id, status
) VALUES (
    #{password}, #{name}, #{email}, #{position}, #{gender}, #{departmentId}, #{status}
)

mapper.select:
SELECT
t.teacher_id,
t.password,
t.name,
t.email,
t.position,
t.gender,
t.department_id,
t.status,
d.department_name
FROM teacher t
LEFT JOIN department d ON t.department_id = d.department_id


        AND t.teacher_id = #{teacherId}


        AND t.name LIKE CONCAT('%', #{teacherName}, '%')


        AND t.gender = #{gender}


        AND t.email = #{email}


        AND t.position = #{position}


        AND t.department_id = #{departmentId}


        AND t.status = #{status}


ORDER BY t.teacher_id
